// @flow
import React from 'react';
import CustomModal from './CustomModal';
import AnimatedContainer from './AnimatedContainer';

class AnimatedModal extends React.Component {
  show = () => this.modal.show();
  hide = () => this.bottomDrawer.hide();

  render() {
    return (
      <CustomModal
        ref={component => (this.modal = component)}
        onShow={() => {
          this.bottomDrawer.show();
        }}
      >
        <AnimatedContainer
          {...this.props}
          onHide={() => this.modal.hide()}
          ref={component => (this.bottomDrawer = component)}
        >
          {this.props.children}
        </AnimatedContainer>
      </CustomModal>
    );
  }
}

export default AnimatedModal;
