// @flow
import React from 'react';
import InteractableContainer from './InteractableContainer';
import CustomModal from './CustomModal';

export class InteractableModal extends React.PureComponent {
  show = () => {
    this.modal.show();
  };

  hide = () => {
    this.drawer.hide();
  };

  onShow = () => {
    this.drawer.show();
  };

  onHide = () => {
    this.modal.hide();
  };

  render() {
    return (
      <CustomModal
        onShow={this.onShow}
        ref={component => (this.modal = component)}
        {...this.props.modalProps}
      >
        <InteractableContainer
          {...this.props}
          onHide={this.onHide}
          ref={component => (this.drawer = component)}
        />
      </CustomModal>
    );
  }
}

export default InteractableModal;
