// @flow
import React from 'react';
import { Platform } from 'react-native';
import CustomDatePickerIOSInteractable from './CustomDatePickerIOSInteractable';
import CustomDatePickerAndroid from './CustomDatePickerAndroid';

class CustomDatePicker extends React.Component {
  render() {
    const Component = Platform.OS === 'ios'
      ? CustomDatePickerIOSInteractable
      : CustomDatePickerAndroid;
    return <Component {...this.props} />;
  }
}

export default CustomDatePicker;
